import React from "react";
import PropTypes from "prop-types";

function KegDetail(props) {
  const { keg } = props;


  let emptyKeg = "";

  if (keg.quantity === 0) {
    emptyKeg = <button> Empty! Refill Please! </button>
  } else {
    emptyKeg = <button onClick={onClickingBuy}>Pour 1 Pint!</button>
  }

  let drinkOverStock = "";

  if (drink.quantity === 124) {
    drinkOverStock = <button className="btn" disabled>Keg Full</button>
  } else {
    drinkOverStock = <button className="btn btn-info" onClick={onClickingRefill}>Refill Keg with 1 Pint.</button>
  }




  return (
    <React.Fragment>
      <h1>Keg Details </h1>
      <h3>{keg.name}</h3>
      <p> Brand: {keg.brand}</p>
      <p> Flavor: {keg.flavor}</p>
      <p> Price: {keg.price}</p>
      <p> {keg.quantity} Pints Remaining</p>
    </React.Fragment>
  )
}

KegDetail.propTypes = {
  keg: PropTypes.object,
  onClickEditKeg: PropTypes.func
}

export default KegDetail;
